import React, { Component } from 'react'
import api from '../api'
import '../style/register.css'
import { NavBar } from './../components'

export default class register extends Component {
    constructor(props) {
        super(props);

        this.state = {
            
            name: "",
            password: "",
            
            user: []
        }
        this.onChangeName = this.onChangeName.bind(this)
        this.onChangePassword = this.onChangePassword.bind(this)
       
        this.onSubmit = this.onSubmit.bind(this)
     
    }

    onChangeName(e) {
       
        this.setState({
            name: e.target.value
        });
    }
    onChangePassword(e) {
        this.setState({
            password: e.target.value
        });
    }
    


     onSubmit  = async(e) => {
        
        e.preventDefault();

        const user = {
            name: this.state.name,
            password: this.state.password
        }
        
            try {
        const input = this.state.name

   await     api.getUsers().then(users=>{
            users.data.data.forEach(element => {
                console.log(element.name+input)
                if(element.name===input){
                   return alert("שגיאה: אין אפשרות להשתמש באותו שם משתמש פעמיים") 
                   
                }
            });

        }) } catch (error) {
            
        }

    await    api.createUser(user).then(data => {

            console.log(' usersList -> render -> users', user)
            return (console.log(user))
        })
        alert("User Added sucssafuly")

        this.setState({
            name: "",
            password: ""
        })
        window.location.reload()
    }
    render() {
        return (
            <div>
                <NavBar/>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Enter UserName: </label>
                        <input type="text"
                            required
                            className="form-control"
                            value={this.state.name}
                            onChange={this.onChangeName}
                        />
                    </div>

                    <div className="form-group">
                        <label>Enter Password: </label>
                        <input type="current-password"
                            required
                            className="form-control"
                            value={this.state.password}
                            onChange={this.onChangePassword}
                        />
                    </div>

                    <div className="form-group">
                        <label>Enter Message: </label>
                        <input type="text"
                           
                            className="form-control"
                            value={this.state.message}
                            onChange={this.onChangeMessage}
                        />
                    </div>

                    <div className="form-group">
                        <input type="submit"
                            value="add User"
                            className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
