  constructor(props) {
    super(props);
    this.state = {
      
      isLoggedIn:false,

      userName: '',
      password: ''
    }


    this.sendToDB = this.sendToDB.bind(this);
  }
  componentDidUpdate = async () => {
    
    await api.getUserByUserName(this.state.userName, this.state.password).then(user => {
