import React, { Component } from 'react';

import {
  BrowserRouter as Router,
  Route,
  Redirect,
} from "react-router-dom";
import PrivateRoute from '../pages/Login/PrivateRoute'
import { Login, Admin, OrdersHistory, Home, ManageUsers, Register } from '../pages'


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      currentUserName: " "
      // currentId: null
    }

    this.changeIsLoggedIn = this.changeIsLoggedIn.bind(this);

    this.changeCurrentUserName= this.changeCurrentUserName.bind(this);
  }

  changeIsLoggedIn() {
    this.setState({
      isLoggedIn: true
    })
  }
  // changeCurrentId(id) {
  //   this.setState({
  //     currentId: id
  //   })
  // }
  changeCurrentUserName(userName) {
    this.setState({
      currentUserName: userName
    })
  }

  render() {
    return (
      <Router>
        <Route exact path="/">

          <Login
            isLoggedIn={this.state.isLoggedIn}
            currentId={this.currentId}
            currentUserName={this.currentUserName}
            changeIsLoggedIn={this.changeIsLoggedIn}
            // changeCurrentId={this.changeCurrentId}
            changeCurrentUserName={this.changeCurrentUserName}
          />

          <PrivateRoute isAuth={this.state.isLoggedIn} path='/home' component={Home} />

          <PrivateRoute exact path="/admin" component={Admin} />

          <PrivateRoute exact path="/admin/register" component={Register} />

          <PrivateRoute exact path="/admin/ManageUsers" component={ManageUsers} />

          <PrivateRoute exact path="/admin/ordershistory" component={OrdersHistory} />
        </Route>



      </Router>
    )
  }
}
export default App
